package org.javaprogramms.basic;

public class OutputFormate {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 1, b = 2, c = 3, d = 4;
		
		System.out.print(a+"\t"+b);
		
		System.out.println(b+"\n"+b);
		
		System.out.print(":"+c);
		
		System.out.println();
		
		System.out.println("Hello\\Hi\""+d);

	}
	
	
}
