package org.javaprogramms.basic;

public class PreAndPostIncrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 
		int x = 1;
		
		System.out.println(x);
		System.out.println(++x);
		System.out.println(x);
		
		int y = 1;
		
		System.out.println(y);
		System.out.println(y++);
		System.out.println(y);
		
		

	}

}
